jcr:root.manageTabs:
function(tab,noSwitch)
{
var tabs=['columns','two_column','three_column_experienceit','three_column_productPage'];
var index=tabs.indexOf(tab);
if(index==-1) return;
for(var i=1;i<tabs.length;i++)
{
if(index==i)
{this.unhideTabStripItem(i);}
else
{this.hideTabStripItem(i);
}
}
this.doLayout();
if(!noSwitch)this.activate(index);}
