listeners.loadcontent:
function(comp,object,value) {
var panel = comp.findParentByType("panel");
 var selection=panel.getComponent("addressline2checbox").getValue();

 var addressline2=panel.getComponent("address1");


 if(selection=='Yes')
 {
 addressline2.show();

 }
 else{
 addressline2.hide();
 }
}

listeners.selectionchanged:
function(comp,object,value) {
var panel = comp.findParentByType("panel");
 var selection=panel.getComponent("addressline2checbox").getValue();

 var addressline2=panel.getComponent("address1");


 if(selection=='Yes')
 {
 addressline2.show();

 }
 else{
 addressline2.hide();
 }
}
